// Service Worker for ThachAI Assistant PWA
const CACHE_NAME = 'thachai-v1';
const urlsToCache = [
  '/',
  '/index.html',
  '/generated-icon.png'
];

self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => cache.addAll(urlsToCache))
  );
});

self.addEventListener('fetch', event => {
  event.respondWith(
    caches.match(event.request)
      .then(response => {
        if (response) {
          return response;
        }
        return fetch(event.request);
      }
    )
  );
});